import React, { useEffect, useState } from 'react';
import { ChakraProvider } from "@chakra-ui/react";
import { Auth } from 'firebase/auth';
import { Route, Routes } from 'react-router-dom';
import PagePrincipal from './PagePrincipal/PagePrincipal';
import Contact from './contact/Contact';
import Navbar from './componment/Navbar';
import Register from './Register/Register';
import Footer from './footer/Footer';
import Market from './MarketPlace.js/Market';
import Market2 from './Market2/Market2';
import Login from './Login/Login';

export default function App() {
  const [userName, setUserName] = useState("");

  useEffect(() => {
    // Assuming 'auth' is your Firebase authentication instance
    const auth = Auth();

    const unsubscribe = auth.onAuthStateChanged(user => {
      if (user) {
        setUserName(user.displayName);
      } else {
        setUserName("");
      }
    });

    return () => unsubscribe();
  }, []);

  return (
    <div>
      <ChakraProvider>
        <Navbar userName={userName} />
        <Routes>
          <Route path='/' element={<PagePrincipal />} />
          <Route path='/contact' element={<Contact />} />
          <Route path='/Market' element={<Market />} />
          <Route path='/Market2' element={<Market2 />} />
          <Route path='/Login' element={<Login />} />
          <Route path='/Register' element={<Register />} />
        </Routes>
        <Footer />
      </ChakraProvider>
    </div>
  );
}
